import './App.css';

function App() {
  return (
    <div>
      <h1>가계부</h1>

      {/* 수입/지출 체크박스 */}
      <div class="radio-section">
        <label>
          <input type='radio' name="transactionType" value='income' checked /> 수입
        </label>
        <label>
          <input type='radio' name="transactionType" value='expense' checked /> 지출
        </label>
      </div>

      {/* 금액 입력 */}
      <div class="input-section">
        <input type="text" id="amount" placeholder="금액 입력" />
        <button id="addButton">등록</button>
      </div>

      {/* 총 금액 */}
      <div class="total-section">
        총 금액: <span id="total">0</span> 원
      </div>
    </div>
  );
}

export default App;
