import './App.css';
import RadioSection from './component/RadioSection';
import InputSection from './component/InputSection';
import TotalSection from './component/TotalSection';

function App() {
  return (
    <div>
      <h1>가계부</h1>

      {/* 수입/지출 체크박스 */}
      <RadioSection/>

      {/* 금액 입력 */}
      <div class="input-section">
        <input type="text" id="amount" placeholder="금액 입력" />
        <button id="addButton">등록</button>
      </div>

      {/* 총 금액 */}
      <div class="total-section">
        총 금액: <span id="total">0</span> 원
      </div>
    </div>
  );
}

export default App;
